mapper.select:
select

    and vehicle_binding_status = #{conf.userType}

from etl_user_vehicle_mapping
where 1=1

    and make_id = #{conf.makeId}


    and car_number like concat('%',#{conf.vehicleNo,jdbcType=VARCHAR},'%')


    and  UNIX_TIMESTAMP(vehicle_sale_date) >= #{conf.saleStartTime} 
    and  UNIX_TIMESTAMP(vehicle_sale_date) <= #{conf.saleEndTime} 


    and  UNIX_TIMESTAMP(last_back_store_time) >= #{conf.lastBackStartTime} 
    and  UNIX_TIMESTAMP(last_back_store_time) <= #{conf.lastBackEndTime} 


    and  last_back_store_mileage >= #{conf.mileageMin} 
    and  last_back_store_mileage <= #{conf.mileageMax}
